import React from "react";

export default class Playlist extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			restaurant: [],
		};
	}

	componentDidMount() {
		const id = this.props.match.params.id;
		fetch(
			`https://my-json-server.typicode.com/landonsm/restaurants-api-final-project/restaurants/${id}`
		)
			.then((response) => {
				return response.json();
			})
			.then((json) => {
				console.log(json);
				this.setState({
					restaurant: json,
				});
			});
	}

	render() {
		return (
			<>
				<h3>{this.state.restaurant.name}</h3>
				<p>{this.state.restaurant.description}</p>
				<p>Cuisine: {this.state.restaurant.cuisine}</p>
				<p>Distance from USC's campus: {this.state.restaurant.hours}</p>
			</>
		);
	}
}
