import React from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStar } from "@fortawesome/free-solid-svg-icons";
import Accordion from "./Accordion";
import { toast } from "react-toastify";

export default class Library extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			restaurants: [],
			openPanel: 1,
			titles: ["1", "2", "3"],
			descriptions: ["1", "2", "3"],
		};
	}

	setPanels(titles, descriptions) {
		console.log("TITLES:");
		console.log(titles);
		console.log("DESCRIPTIONS:");
		console.log(descriptions);
		let panel1Title = titles[0];
		console.log(panel1Title);
		let panel2Title = titles[1];
		console.log(panel2Title);
		let panel3Title = titles[2];
		console.log(panel3Title);
		let panel1Description = descriptions[0];
		let panel2Description = descriptions[1];
		let panel3Description = descriptions[2];
		console.log(panel1Description);
		console.log(panel2Description);
		console.log(panel3Description);

		let tempTitles = [panel1Title, panel2Title, panel3Title];
		console.log(tempTitles);
		let tempDescriptions = [
			panel1Description,
			panel2Description,
			panel3Description,
		];
		console.log(tempDescriptions);

		this.setState({
			titles: tempTitles,
			descriptions: tempDescriptions,
		});
	}

	// https://my-json-server.typicode.com/landonsm/restaurants-api-final-project/restaurants
	componentDidMount() {
		fetch("http://localhost:4000/api/restaurants", {
			headers: {
				"Content-type": "application/json",
			},
		})
			.then((response) => {
				return response.json();
			})
			.then((json) => {
				console.log("json");
				console.log(json);
				console.log(json[0].name);
				console.log(json[1].name);

				let tempTitlesArray = [];

				for (let i = 0; i <= json.length; i++) {
					console.log(json[i].name);
					tempTitlesArray.push(json[i].name);
				}

				this.setState({
					restaurants: json,
					titles: tempTitlesArray,
				});
			})
			.catch((error) => console.error(error));
	}

	render() {
		let arrayOfTitles = [];
		let arrayOfDescriptions = [];
		return (
			<div className="container">
				<table className="table my-3 table-striped">
					<thead>
						<tr>
							<th>Restaurant</th>
							<th>Cuisine</th>
							<th>Location</th>
							<th>Rating</th>
							<th>Favorite</th>
						</tr>
					</thead>
					<tbody>
						{this.state.restaurants.map((restaurant) => {
							// console.log(restaurant.name);
							arrayOfTitles.push(restaurant.name);
							// console.log(arrayOfTitles);
							// console.log(restaurant.description);
							arrayOfDescriptions.push(restaurant.description);
							// console.log(arrayOfDescriptions);
							return (
								<tr>
									<td key={restaurant.id} className="td-element">
										<Link to={`/restaurants/${restaurant.id}`}>{restaurant.name}</Link>
									</td>
									<td className="td-element">{restaurant.cuisine}</td>
									<td className="td-element">{restaurant.location}</td>
									<td className="td-element">{restaurant.rating}</td>
									<td className="td-element">
										<div className="btn-group">
											<Link
												onClick={(event) => {
													console.log("OPEN MODAL");
													this.setState({ isModalOpen: true });
												}}
												to={`/restaurants/${restaurant.id}/favoriteChange`}
												className="btn"
											>
												<FontAwesomeIcon
													icon={faStar}
													color={restaurant.favorite ? "yellow" : "black"}
													size="2x"
													className="star"
												/>
											</Link>
										</div>
									</td>
								</tr>
							);
						})}
					</tbody>
				</table>
				<div className="row accordion-container">
					<div className="col-sm-8 accordion-col my-3">
						<h4>Popular Today</h4>
						{/* {console.log(arrayOfTitles)}
						{console.log(arrayOfDescriptions)} */}
						{/* {this.setState({ titles: ["1", "2", "3"] })} */}
						{() => this.setPanels(arrayOfTitles, arrayOfDescriptions)}
						{/* {this.setPanels(arrayOfTitles, arrayOfDescriptions)} */}
						{console.log(`OUTPUT`)}
						{console.log(`${this.state.descriptions}`)}
						{console.log(`${this.state.titles}`)}
						{/* {console.log(`${this.state.random}`)} */}
						{/* {this.setState({ descriptions: arrayOfDescriptions })} */}
						{/* {console.log(`${this.state.random}`)} */}
						<Accordion
							className="my-3"
							description={this.state.descriptions}
							title={this.state.titles}
							value={this.state.openPanel}
							onPanelClick={(clickedPanel) => {
								this.setState({ openPanel: clickedPanel });
							}}
							display="none"
						/>
					</div>
				</div>
			</div>
		);
	}
}
