import React from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStar } from "@fortawesome/free-solid-svg-icons";
import { toast } from "react-toastify";
import Modal from "./Modal";

export default class Library extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			restaurants: [],
			isModalOpen: false,
		};
	}

	// https://my-json-server.typicode.com/landonsm/restaurants-api-final-project/restaurants
	componentDidMount() {
		fetch("http://localhost:4000/api/restaurants")
			.then((response) => {
				return response.json();
			})
			.then((json) => {
				this.setState({
					restaurants: json,
				});
			})
			.catch((error) => console.error(error));
	}

	render() {
		return (
			<div className="container">
				<table className="table my-3 table-striped">
					<thead>
						<tr>
							<th>Restaurant</th>
							<th>Cuisine</th>
							<th>Location</th>
							<th>Rating</th>
							<th>Favorite</th>
						</tr>
					</thead>
					<tbody>
						{this.state.restaurants.map((restaurant) => {
							return (
								<tr>
									<td key={restaurant.id} className="td-element">
										<Link to={`/restaurants/${restaurant.id}`}>{restaurant.name}</Link>
									</td>
									<td className="td-element">{restaurant.cuisine}</td>
									<td className="td-element">{restaurant.location}</td>
									<td className="td-element">{restaurant.rating}</td>
									<td className="td-element">
										<div className="btn-group">
											<Link
												to={`/restaurants/${restaurant.id}/favoriteChange`}
												className="btn"
											>
												<FontAwesomeIcon
													icon={faStar}
													color={restaurant.favorite ? "yellow" : "black"}
													size="2x"
													className="star"
												/>
											</Link>
										</div>
										<button
											className="btn"
											onClick={(event) => {
												this.setState({ isModalOpen: true });
												// this.handleFavoritesChange(event, restaurant.id);
											}}
										></button>

										{this.state.isModalOpen && (
											<Modal
												title="David's title"
												body={() => {
													return (
														<div>
															<p>Modal body text goes here.</p>
															<p>Modal body text goes here.</p>
														</div>
													);
												}}
												onClose={() => {
													this.setState({ isModalOpen: false });
												}}
											/>
										)}
									</td>
								</tr>
							);
						})}
					</tbody>
				</table>
			</div>
		);
	}
}
