import React from "react";
import { toast } from "react-toastify";

export default class Playlist extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			restaurant: [],
			// hours: [],
			// meals: [],
		};
	}

	// https://my-json-server.typicode.com/landonsm/restaurants-api-final-project/restaurants/
	componentDidMount() {
		const id = this.props.match.params.id;
		fetch(`http://localhost:4000/api/restaurants/${id}`)
			.then((response) => {
				return response.json();
			})
			.then((json) => {
				// console.log(json.meals[0]);
				this.setState({
					restaurant: json,
					// hours: json.hours[0],
					// meals: json.meals[0],
				});
			});
	}

	deleteRestaurant() {
		const isDeleteConfirmed = window.confirm(
			"Are you sure you want to delete this post?"
		);

		if (!isDeleteConfirmed) {
			return;
		}

		const id = this.props.match.params.id;
		// https://my-json-server.typicode.com/landonsm/restaurants-api-final-project/restaurants/
		const url = `http://localhost:4000/api/restaurants/${id}`;
		console.log(`URL: '${url}`);
		fetch(`http://localhost:4000/api/restaurants/${id}`, {
			method: "DELETE",
		}).then(() => {
			console.log("delete AJAX request");
			console.log(`${this.state.restaurant.id}`);
			console.log(`${id}`);
			console.log("DELETING");
			toast.success(`"${this.state.restaurant.name}" was deleted`);
			this.props.history.push("/");
		});
	}

	render() {
		return (
			<>
				<h3>{this.state.restaurant.name}</h3>
				<p>{this.state.restaurant.description}</p>
				<p>Cuisine: {this.state.restaurant.cuisine}</p>
				{/* <p>
					Open for Breakfast: {this.state.meals.breakfast}, Lunch:{" "}
					{this.state.meals.lunch}, Dinner: {this.state.meals.dinner}
				</p> */}
				<p>Distance from USC's campus: {this.state.restaurant.distFromUsc} Miles</p>
				<p>Location: {this.state.restaurant.location}</p>
				<p>Address: {this.state.restaurant.address}</p>
				<p>Rating: {this.state.restaurant.rating} Stars</p>
				<a href={this.state.restaurant.menu}>Menu</a>
				{/* <p>Hours:</p>
				<ul>
					<li>Monday: {this.state.hours.monday}</li>
					<li>Tuesday: {this.state.hours.tuesday}</li>
					<li>Wednesday: {this.state.hours.wednesday}</li>
					<li>Thursday: {this.state.hours.thursday}</li>
					<li>Friday: {this.state.hours.friday}</li>
					<li>Saturday: {this.state.hours.saturday}</li>
					<li>Sunday: {this.state.hours.sunday}</li>
				</ul> */}
				<div className="btn-group">
					<button
						type="button"
						className="btn btn-danger"
						onClick={() => {
							this.deleteRestaurant();
						}}
					>
						Delete
					</button>
				</div>
			</>
		);
	}
}
