import React from "react";
import { toast } from "react-toastify";

export default class NewRestaurant extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			name: "",
			description: "",
		};
	}

	handleTitleChange(event) {
		this.setState({ title: event.target.value });
	}
	handleBodyChange(event) {
		this.setState({ body: event.target.value });
	}
	handleSubmit(event) {
		event.preventDefault();

		fetch(
			"https://my-json-server.typicode.com/landonsm/restaurants-api-final-project/restaurants/",
			{
				method: "POST",
				body: JSON.stringify({
					name: this.state.name,
					description: this.state.description,
				}),
				headers: {
					"Content-type": "application/json",
				},
			}
		)
			.then((response) => {
				return response.json();
			})
			.then((json) => {
				console.log(json);
				// toast.success(`Post "${json.title}" was successfully created`);
				this.props.history.push("/");
			});
	}

	render() {
		return (
			<form
				onSubmit={(event) => {
					this.handleSubmit(event);
				}}
			>
				<div className="my-3">
					<label htmlFor="name" className="form-label">
						Title
					</label>
					<input
						id="name"
						className="form-control"
						value={this.state.name}
						onChange={(event) => {
							this.handleNameChange(event);
						}}
					/>
				</div>
				<div className="my-3">
					<label htmlFor="description" className="form-label">
						Body
					</label>
					<textarea
						id="description"
						className="form-control"
						value={this.state.description}
						onChange={(event) => {
							this.handleDescriptionChange(event);
						}}
					></textarea>
				</div>
				<button type="submit" className="btn btn-primary">
					Add Restaurant
				</button>
			</form>
		);
	}
}
