import React from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStar } from "@fortawesome/free-solid-svg-icons";
import { toast } from "react-toastify";

export default class Library extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			restaurants: [],
		};
	}

	handleFavoritesChange(event, id) {
		event.preventDefault();

		// https://my-json-server.typicode.com/landonsm/restaurants-api-final-project/restaurants/${id}
		fetch(`http://localhost:4000/api/restaurants/${id}`, {
			method: "PUT",
			body: JSON.stringify({
				name: this.state.name,
				description: this.state.description,
				cuisine: this.state.cuisine,
				meals: this.state.meals,
				location: this.state.location,
				address: this.state.address,
				distFromUsc: this.state.distFromUsc,
				rating: this.state.rating,
				favorite: this.state.favorite,
				menu: this.state.menu,
				dateAdded: this.state.dateAdded,
				hours: this.state.hours,
				authorId: this.state.authorId,
			}),
			headers: {
				"Content-type": "application/json",
			},
		})
			.then((response) => {
				console.log("Start 1");
				console.log(response);
				console.log(response.json);
				console.log("End 1");
				return response.json();
			})
			.then((json) => {
				console.log("Start 2");
				console.log(json);
				toast.success(`"${json.name}" was added to favorites`);
				console.log("End 2");
			});
	}

	// https://my-json-server.typicode.com/landonsm/restaurants-api-final-project/restaurants
	componentDidMount() {
		fetch("http://localhost:4000/api/restaurants")
			.then((response) => {
				return response.json();
			})
			.then((json) => {
				console.log(json);
				this.setState({
					restaurants: json,
				});
			});
	}

	render() {
		return (
			<div className="container">
				<table className="table my-3 table-striped">
					<thead>
						<tr>
							<th>Restaurant</th>
							<th>Cuisine</th>
							<th>Location</th>
							<th>Rating</th>
							<th>Favorites</th>
						</tr>
					</thead>
					<tbody>
						{this.state.restaurants.map((restaurant) => {
							console.log(restaurant.name);
							return (
								<tr>
									<td key={restaurant.id} className="td-element">
										<Link to={`/restaurants/${restaurant.id}`}>{restaurant.name}</Link>
									</td>
									<td className="td-element">{restaurant.cuisine}</td>
									<td className="td-element">{restaurant.location}</td>
									<td className="td-element">{restaurant.rating}</td>
									<td className="td-element">
										<button
											className="btn"
											onClick={(event) => {
												this.handleFavoritesChange(event, restaurant.id);
											}}
										>
											<FontAwesomeIcon
												icon={faStar}
												color={restaurant.favorite ? "yellow" : "black"}
												size="2x"
												className="star"
											/>
										</button>
									</td>
								</tr>
							);
						})}
					</tbody>
				</table>
			</div>
		);
	}
}
