import React from "react";
import { toast } from "react-toastify";

export default class EditRestaurant extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			name: "",
			description: "",
			cuisine: "",
			meals: [
				{
					breakfast: "",
					lunch: "",
					dinner: "",
				},
			],
			location: "",
			address: "",
			distFromUsc: "",
			rating: "",
			favorite: "",
			menu: "",
			dateAdded: "",
			hours: [
				{
					monday: [
						{
							open: "",
							close: "",
						},
					],
					tuesday: [
						{
							open: "",
							close: "",
						},
					],
					wednesday: [
						{
							open: "",
							close: "",
						},
					],
					thursday: [
						{
							open: "",
							close: "",
						},
					],
					friday: [
						{
							open: "",
							close: "",
						},
					],
					saturday: [
						{
							open: "",
							close: "",
						},
					],
					sunday: [
						{
							open: "",
							close: "",
						},
					],
				},
			],
			authorId: "",
		};
	}

	handleSubmit(event) {
		event.preventDefault();

		const newFav = true;

		if (this.state.favorite) {
			newFav = false;
		}

		const id = this.props.match.params.id;
		// https://my-json-server.typicode.com/landonsm/restaurants-api-final-project/restaurants/${id}
		fetch(`http://localhost:4000/api/restaurants/${id}`, {
			method: "PUT",
			body: JSON.stringify({
				name: this.state.name,
				description: this.state.description,
				cuisine: this.state.cuisine,
				meals: this.state.meals,
				location: this.state.location,
				address: this.state.address,
				distFromUsc: this.state.distFromUsc,
				rating: this.state.rating,
				favorite: newFav,
				menu: this.state.menu,
				dateAdded: this.state.dateAdded,
				hours: this.state.hours,
				authorId: this.state.authorId,
			}),
			headers: {
				"Content-type": "application/json",
			},
		})
			.then((response) => {
				console.log("Start 1");
				console.log(response);
				console.log(response.json);
				console.log("End 1");
				return response.json();
			})
			.then((json) => {
				console.log("Start 2");
				console.log(json);
				toast.success(`"${json.name}" was updated`);
				console.log("End 2");
				this.props.history.push("/");
			});
	}

	// https://my-json-server.typicode.com/landonsm/restaurants-api-final-project/restaurants/
	componentDidMount() {
		const id = this.props.match.params.id;
		fetch(`http://localhost:4000/api/restaurants/${id}`)
			.then((response) => {
				return response.json();
			})
			.then((json) => {
				console.log(json);
				this.setState(json);
			});
	}

	render() {
		return (
			<form
				className="form-inline"
				onSubmit={(event) => {
					this.handleSubmit(event);
				}}
			>
				<div className="container">
					<div className="row form-row my-3">
						<div className="col-sm-12">
							<button type="submit" className="btn btn-primary btn-block">
								Update Restaurant
							</button>
						</div>
					</div>
				</div>
			</form>
		);
	}
}
