import React from "react";
import { toast } from "react-toastify";

export default class NewRestaurant extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			name: "",
			description: "",
			cuisine: "",
			meals: [
				{
					breakfast: false,
					lunch: false,
					dinner: false,
				},
			],
			location: "",
			address: "",
			distFromUsc: "",
			rating: "",
			favorite: false,
			menu: "",
			dateAdded: "",
			hours: [
				{
					monday: [
						{
							open: "",
							close: "",
						},
					],
					tuesday: [
						{
							open: "",
							close: "",
						},
					],
					wednesday: [
						{
							open: "",
							close: "",
						},
					],
					thursday: [
						{
							open: "",
							close: "",
						},
					],
					friday: [
						{
							open: "",
							close: "",
						},
					],
					saturday: [
						{
							open: "",
							close: "",
						},
					],
					sunday: [
						{
							open: "",
							close: "",
						},
					],
				},
			],
			authorId: "0",
		};
	}

	handleNameChange(event) {
		this.setState({ name: event.target.value });
	}
	handleDescriptionChange(event) {
		this.setState({ description: event.target.value });
	}
