import React from "react";
import { toast } from "react-toastify";

export default class NewRestaurant extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			name: "",
			description: "",
		};
	}

	handleNameChange(event) {
		this.setState({ name: event.target.value });
	}
	handleDescriptionChange(event) {
		this.setState({ description: event.target.value });
	}
	handleSubmit(event) {
		event.preventDefault();
		// https://my-json-server.typicode.com/landonsm/restaurants-api-final-project/restaurants
		fetch("http://localhost:4000/api/restaurants", {
			method: "POST",
			body: JSON.stringify({
				name: this.state.name,
				description: this.state.description,
			}),
			headers: {
				"Content-type": "application/json",
			},
		})
			.then((response) => {
				console.log("Start 1");
				console.log(response);
				console.log(response.json);
				console.log("End 1");
				return response.json();
			})
			.then((json) => {
				console.log("Start 2");
				console.log(json);
				toast.success(`Post "${json.name}" was successfully created`);
				console.log("End 2");
				// this.props.history.push("/");
			});
	}

	render() {
		return (
			<form
				onSubmit={(event) => {
					this.handleSubmit(event);
				}}
			>
				<div className="my-3">
					<label htmlFor="name" className="form-label">
						Name
					</label>
					<input
						id="name"
						className="form-control"
						value={this.state.name}
						onChange={(event) => {
							this.handleNameChange(event);
						}}
					/>
				</div>
				<div className="my-3">
					<label htmlFor="description" className="form-label">
						Description
					</label>
					<textarea
						id="description"
						className="form-control"
						value={this.state.description}
						onChange={(event) => {
							this.handleDescriptionChange(event);
						}}
					></textarea>
				</div>
				<button type="submit" className="btn btn-primary">
					Add Restaurant
				</button>
			</form>
		);
	}
}
